document.addEventListener("DOMContentLoaded",function(){function t(){document.getElementById("mobile-menu").style.width="100%",document.getElementById("mobile-menu-overlay").style.display="block",document.getElementById("mobile-menu-overlay").style.opacity="100%"}function o(){document.getElementById("mobile-menu").style.width="0",document.getElementById("mobile-menu-overlay").style.display="none",document.getElementById("mobile-menu-overlay").style.opacity="0"}window.openMobileMenu=t,window.closeMobileMenu=o;let n=document.getElementsByClassName("mobile-menu-accordion-toggle");for(let e=0;e<n.length;e++)n[e].addEventListener("click",function(){let i=n[e].parentElement.querySelector(".mobile-menu-accordion"),l=n[e].parentElement.nextElementSibling;i.classList.toggle("active"),n[e].classList.toggle("active"),l.style.display==="block"?l.style.display="none":l&&(l.style.display="block")})});
