document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".popup");setTimeout(function(){const n=function(){e.forEach(function(o){o.style.display="block"}),window.removeEventListener("scroll",n)};window.addEventListener("scroll",n)},300),e.forEach(function(n){const o=n.querySelector(".popup-close-button");o&&o.addEventListener("click",function(){n.style.display="none"})})});
